/*
  Note: We have included the plugin in the same JavaScript file as the TinyMCE
  instance for display purposes only. Tiny recommends not maintaining the plugin
  with the TinyMCE instance and using the `external_plugins` option.
*/
tinymce.PluginManager.add('btn_copy', (editor, url) => {
    const openDialog = () => editor.windowManager.open({
        title: 'Button copy',
        body: {
            type: 'panel',
            items: [
                {
                    type: 'input',
                    name: 'content',
                    label: 'Ná»™i dung copy'
                }
            ]
        },
        buttons: [
            {
                type: 'cancel',
                text: 'Close'
            },
            {
                type: 'submit',
                text: 'Insert',
                buttonType: 'primary'
            }
        ],
        onSubmit: (api) => {
            const data = api.getData();
            /* Insert content when the window form is submitted */
            editor.insertContent(`[btn_copy content="${data.content}"]`);
            api.close();
        }
    });
    /* Add a button that opens a window */
    editor.ui.registry.addButton('btn_copy', {
        icon: 'copy',
        tooltip: 'Button copy',
        onAction: () => {
            /* Open window */
            openDialog();
        }
    });
    /* Adds a menu item, which can then be included in any menu via the menu/menubar configuration */
    editor.ui.registry.addMenuItem('btn_copy', {
        text: 'Button copy',
        onAction: () => {
            /* Open window */
            openDialog();
        }
    });
    /* Return the metadata for the help plugin */
    return {
        getMetadata: () => ({
            name: 'Button copy',
            url: 'https://simthanglong.vn'
        })
    };
});

